catalog_ui_policy.script_false:
function onCondition() {
	var temp = g_form.getValue("temperature_check");
	if(temp){
		g_form.setValue("temperature_check",false);
	}
}

catalog_ui_policy.script_true:
function onCondition() {
	var temp = g_form.getValue("temperature_check");
	gs.addInfoMessage('Nice');
	if(!temp){
		g_form.setValue("temperature_check",true);
	gs.addInfoMessage('Nice dentro');
	}
}
