function getUserItem(user) {

    if (producer.user_not_found != 'true') {
        //Look for existing record
        var gr = new GlideRecord('x_817226_segcov_ui');
        gr.addQuery('owned_by', user);
        gr.addQuery('sys_class_name', 'x_817226_segcov_ui');
        gr.query();
        if (gr.next()) {
            return gr.sys_id;
        }
    } else {
        //check User
        var gr2 = new GlideRecord('sys_user');
        gr2.addQuery('u_vp', producer.department);
        gr2.addQuery('name', producer.user_name);
        gr2.query();
        if (gr2.next()) {
            var ui = new GlideRecord('x_817226_segcov_ui');
            ui.addQuery('owned_by', gr2.sys_id);
            ui.addQuery('sys_class_name', 'x_817226_segcov_ui');
            ui.query();
            if (ui.next()) {
                return ui.sys_id;
            }
        }
    }
    return createUI();
}

function createUI() {

    var ui = new GlideRecord('x_817226_segcov_ui');
    ui.initialize();
    ui.owned_by = producer.owned_by;
    ui.name = producer.owned_by.getDisplayValue();
    if (producer.user_not_found == 'true') {
        ui.name = producer.user_name;
        var user = checkCreateUser();
        ui.owned_by = user;
    }
    ui.location = producer.location;
    ui.department = producer.department;

    //Set user field values
    ui.u_user_location = producer.location.getDisplayValue();
    ui.u_user_name = ui.name;
    ui.u_user_number = producer.employee_number;
    ui.u_user_title = producer.user_title;
    ui.user_vicepresidency = producer.department.getDisplayValue();
    ui.u_user_category = producer.user_category;

    ui.assignment_group = '8f859f6997011110321635dfe153afc7'; // CX BSO MX Personal Medico
    ui.comments = 'Creado por ' + gs.getUserDisplayName() + ' (' + gs.getUserName() + ') ' + 'por kit entregado en: ' + producer.location.getDisplayValue() + ' el dia ' + new GlideDateTime();
    var uiID = ui.insert();
    return uiID;
}

function checkCreateUser() {
    var id = 'cxbsomx.new.' + producer.user_name;
    var usr = new GlideAggregate('sys_user');
    usr.addAggregate('COUNT');
    usr.addEncodedQuery('user_nameSTARTSWITH' + id);
    var count = 0;
    usr.query();
    if (usr.next()) {
        count = usr.getAggregate('COUNT');
        count++;
        id += count;
    }
    if (producer.employee_number != '') {
        var gr = new GlideRecord('sys_user');
        if (!gr.get('employee_number', producer.employee_number)) {
            return '';
            gr.initialize();
            gr.first_name = producer.user_name;
            gr.employee_number = producer.employee_number;
            gr.user_name = id;
            gr.location = producer.location;
            gr.department = producer.department;
            gr.x_817226_segcov_u_vp = producer.department;
            gr.x_817226_segcov_user_category = producer.user_category;
            gr.title = producer.user_title.getDisplayValue();
            gr.x_817226_segcov_u_business_unit = '2bfebe101b62d054c28b997e0d4bcbdb';
            //var user = gr.insert();
            //return user;
        }
    } else {
        var gr2 = new GlideRecord('sys_user');
        gr2.addQuery('x_817226_segcov_u_vp', producer.department);
        gr2.addQuery('name', producer.user_name);
        gr2.query();
        if (gr2.next()) {
            return gr2.sys_id;
        } else {
            return '';
            gr2.newRecord();
            gr2.first_name = producer.user_name;
            gr2.employee_number = producer.employee_number;
            gr2.user_name = id;
            gr2.location = producer.location;
            gr2.department = producer.department;
            gr2.x_817226_segcov_u_vp = producer.department;
            gr2.x_817226_segcov_user_category = producer.user_category;
            gr2.title = producer.user_title.getDisplayValue();
            gr2.x_817226_segcov_u_business_unit = '2bfebe101b62d054c28b997e0d4bcbdb';
            // gr2.insert();
            //return gr2.getUniqueValue();
        }
    }
}
